else.script:

var id = mc.getProperty('uri.var.id');
var patient = mc.getProperty('uri.var.patient');
var category = mc.getProperty('uri.var.category');
var clinicalStatus = mc.getProperty('uri.var.clinicalStatus');
var startOnset = mc.getProperty('uri.var.startOnset');
var endOnset = mc.getProperty('uri.var.endOnset');
urlQuery = '';
if  (id != null && id != "") {
    urlQuery += '_id=' + id + '&';
}
if  (patient != null && patient != "") {
    urlQuery += 'patient=' + patient + '&';
}
if  (category != null && category != "") {
    urlQuery += 'category=' + category + '&';
}
if  (clinicalStatus != null && clinicalStatus != "") {
    urlQuery += 'clinicalStatus=' + clinicalStatus + '&';
}
if  (startOnset != null && startOnset != "") {
    urlQuery += 'onset=gt' + startOnset + '&';
}
if  (endOnset != null && endOnset != "") {
    urlQuery += 'onset=lt' + endOnset + '&';
}
if  (urlQuery != null && urlQuery  != "") {
    urlQuery = '?' + urlQuery.toString().substring(0, urlQuery.toString().lastIndexOf("&"));
}
mc.setProperty('uri.var.urlQuery',urlQuery);
