else.script:

var id = mc.getProperty('uri.var.id');
var patient = mc.getProperty('uri.var.patient');
var status = mc.getProperty('uri.var.status');
var dateWritten = mc.getProperty('uri.var.dateWritten');
urlQuery = '';
if  (id != null && id != "") {
    urlQuery += '_id=' + id + '&';
}
if  (patient != null && patient != "") {
    urlQuery += 'patient=' + patient + '&';
}
if  (status != null && status != "") {
    urlQuery += 'status=' + status + '&';
}
if  (dateWritten != null && dateWritten != "") {
    urlQuery += 'dateWritten=' + dateWritten + '&';
}
if  (urlQuery != null && urlQuery  != "") {
    urlQuery = '?' + urlQuery.toString().substring(0, urlQuery.toString().lastIndexOf("&"));
}
mc.setProperty('uri.var.urlQuery',urlQuery);
